package week1.day2;

public class LearnIteration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//type for and ctrl+space  -- select 2nd option
		//i=1
		//i<=10 -- till condition is true
		//i++ - increment - increment by 1
				
		for (int i = 1; i<=10; i++) {
			System.out.println(i);
		}
		
		System.out.println("------------------------------");
		
		for (int i = 10; i >=1; i--) {
			System.out.println(i);
			
		}
	}
	

}
